mapper.select:
select *
from teacher


        and name = #{name}


        and level = #{level}


        and join_date between #{joinDateBegin} and #{joinDateEnd}

    and is_deleted != 1

order by join_date desc

select count(name)
from teacher
where name = #{name}

    and id != ${id}

and is_deleted != 1

mapper.update:
update teacher
set avatar = #{path}
where id = #{id}

update teacher set is_deleted = 1

    id in(

        #{id}

    )
